#define STYLE "\n\
<style type=\"text/css\" >\n\
	html,body{\n\
        padding: 0;\n\
        margin: 0;\n\
        height: 100%;\n\
        width: 100%;\n\
        background-color: #F7F7F7;\n\
        color: #333;\n\
        font-family: \"Helvetica Neue\",Helvetica,Arial,sans-serif;\n\
        font-size: 14px;\n\
        line-height: 1.42857;\n\
    }\n\
    canvas{\n\
        width: 100%;\n\
        height: 100%;\n\
    }\n\
    .top{\n\
        margin-bottom: 50px;\n\
        text-align: center;\n\
        padding: 15px 0 0;\n\
        color: #818181;\n\
        font-weight: bold;\n\
        font-size: 16pt;\n\
    }\n\
    .container{\n\
        width: 560px;\n\
        height: 600px;\n\
        margin: 0 auto;\n\
        border-radius: 5px 5px 0 0;\n\
        box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.55);\n\
    }\n\
    .container-head{\n\
        width: 100%;\n\
        padding: 10px 0;\n\
        text-align: center;\n\
        text-transform: uppercase;\n\
        background-color: #799AFF;\n\
        color: #fff;\n\
        border-radius: 4px 4px 0 0;\n\
        border: none;\n\
        border-bottom: 2px solid #5375E0;\n\
        border-top: 1px solid #C8D5FF;\n\
    }\n\
    .container-body{\n\
        background-color: #fff;\n\
        height: 550px;\n\
        border-top: none;\n\
        width: auto;\n\
        padding: 5px;\n\
    }\n\
    .footer{\n\
        text-align: center;\n\
        padding: 50px 0 50px;\n\
        color: #818181;\n\
        font-size: 14px;\n\
    }\n\
</style>"
